import React from 'react';
import Card from '@mui/material/Card';
import {FeatureToggle, Off, On} from "../FeatureToggle/FeatureToggle";
import {MyFeatures} from "../../utils/MyFeatures";
import OrderDialog from "../OrderDialog/OrderDialog";

import "./BookCard.css"

export interface BookInfo {
    name: string;
    author: string;
    price: number;
}

const cardStyle: React.CSSProperties = {
    padding: '10px',
    display: 'flex',
    flexDirection:'column',
    justifyContent:'center'
}

const BookCard: React.FC<BookInfo> = ({ name, author, price }) => {
    return (
        <Card style={{background: 'white', minHeight: '184px', fontFamily: '"Open Sans", serif' }}  data-testid="bookCard">
            <div style={cardStyle}>
                <div>
                    <h4 className='truncate bookName' data-testid="bookName">{name}</h4>
                    <p className='truncate author' data-testid="author">{author}</p>
                </div>
                <p style={{fontWeight: 'bold', color: '#E64D3C', marginBottom: '0px'}}
                   data-testid="price">{price} EUR</p>
                <FeatureToggle name={MyFeatures.ORDER_BUTTON}>
                    <On>
                        <OrderDialog/>
                    </On>
                    <Off>
                        <></>
                    </Off>
                </FeatureToggle>
            </div>
        </Card>
    )
}

export default BookCard;
