import React from 'react';
import BookCard, { BookInfo } from '../BookCard/BookCard';

export interface BookList {
    items: BookInfo[];
}

const gridStyle: React.CSSProperties = {
    display: 'grid',
    gridTemplateColumns: 'repeat(5, 236px)',
    rowGap: '50px',
    columnGap: '30px',
    justifyContent: 'center'
}

const BookList: React.FC<BookList> = ({ items }) => {

    return (
        <div data-testid="grid" style={gridStyle}>
            {items.map((i, index) => (
                <div key={index} data-testid="bookItem">
                    <BookCard name={i.name} author={i.author} price={i.price}></BookCard>
                </div>
            ))}
        </div>
    )
}

export default BookList;
